import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api'; // replace with your backend API URL

const authService = {
  resetPassword: async (id, token, password) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/reset-password/${id}/${token}`, { password });
      return response.data;
    } catch (error) {
      throw error;
    }
  }
};

export default authService;
