import React, { useEffect } from 'react'
import { useLocation } from 'react-router-dom'
import queryString from 'query-string'
import axios from 'axios'


const API_BASE_URL = process.env.API_BASE_URL || 'http://localhost:3600/api'
function Form() {
  const Location = useLocation()
  console.log(Location.search)

  const verifyToken=async ()=> {
    try {
      
    
    const {token, id}= queryString.parse(Location.search)
    console.log(token,"                                                          /n", id)
    console.log(process.env.API_BASE_URL)
    const res = await axios(`${API_BASE_URL}/verify-token?token=${token}&id=${id}`)
    console.log(res)

  } catch (error) {
    console.log(error)
      
  }



  }
  useEffect(() => {
    verifyToken()
  
  }, [])
  
  return (
    <div className="max-w-screen-sm m-auto pt-40">
      <h1 className='text-center text-3xl text-gray-500 mb-3' >
        Reset password
      </h1>
      <form className='shadow w-full rounded-lg p-10'>
         <div className='space-y-8'>

        <input
          type="password"
          placeholder="New password"
          className='px-3 text-lg h-10 w-full border-gray-500 border-2 rounded'
          />
        <input
          type="password"
          placeholder="Confirm password"
          className='px-3 text-lg h-10 w-full border-gray-500 border-2 rounded'
          />
        <button className='bg-blue-500 text-white text-lg h-10 w-full rounded mt-3'>
          Reset password
        </button>
        

          </div>
      </form>


    </div>
    
  )
}

export default Form
