import { useParams } from "react-router-dom";
import Form from "./components/Form";
import { useToasts } from "react-toast-notifications";
import { useForm } from "react-hook-form";
import { useState } from "react";
import authService from "./services/authService";
// import { useHistory } from 'react-router';



function App() {
  const { token="fefd", id="defdf" } = useParams();
  // const history = useHistory();
  const { addToast } = useToasts();
  const { register, handleSubmit, watch, errors } = useForm();
  const [isLoading, setIsLoading] = useState(false);

  const onSubmit = async (data) => {
    setIsLoading(true);
    try {
      await authService.resetPassword(id, token, data.password);
      addToast('Password reset successfully!', { appearance: 'success' });
      // history.push('/login');
    } catch (error) {
      addToast(error.message, { appearance: 'error' });
    }
    setIsLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
          Reset your password
        </h2>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
          {/* <form className="space-y-6" onSubmit={handleSubmit(onSubmit)}>
            <input type="hidden" name="id" value={id} ref={register} />
            <input type="hidden" name="token" value={token} ref={register} />

            <div>
              <label
                htmlFor="password"
                className="block text-sm font-medium text-gray-700"
              >
                New Password
              </label>
              <div className="mt-1">
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="new-password"
                  ref={register({
                    required: 'Please enter a password',
                    minLength: {
                      value: 8,
                      message: 'Your password must be at least 8 characters long',
                    },
                  })}
                  className={`${
                    errors.password
                      ? "border-red-300 focus:ring-red-500 focus:border-red-500"
                      : "border-gray-300 focus:ring-primary-500 focus:border-primary-500"
                  } rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 sm:text-sm w-full`}
                />
                {errors.password && (
                  <span className="text-red-600 text-xs mt-1">
                    {errors.password.message}
                  </span>
                )}
              </div>
            </div>

            <div>
              <label
                htmlFor="confirmPassword"
                className="block text-sm font-medium text-gray-700"
              >
                Confirm Password
              </label>
              <div className="mt-1">
                <input
                  id="confirmPassword"
                  name="confirmPassword"
                  type="password"
                  autoComplete="new-password"
                  ref={register({
                    validate: (value) =>
                      value === watch('password') || "The passwords don't match",
                  })}
                  className={`${
                    errors.confirmPassword
                      ? "border-red-300 focus:ring-red-500 focus:border-red-500"
                      : "border-gray-300 focus:ring-primary-500 focus:border-primary-500"
                  } rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 sm:text-sm w-full`}
                  />
                  {errors.confirmPassword && (
                    <span className="text-red-600 text-xs mt-1">
                      {errors.confirmPassword.message}
                    </span>
                  )}
                </div>
              </div>

              <div>
                <button
                  type="submit"
                  disabled={isLoading}
                  className={`${
                    isLoading
                      ? "bg-gray-400 cursor-not-allowed"
                      : "bg-primary-500 hover:bg-primary-600"
                  } w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500`}
                >
                  {isLoading ? 'Loading...' : 'Reset password'}
                </button>
              </div>
            </form> */}
          </div>
        </div>
      </div>
    );
  };

export default App;
